import React from 'react'
import './App.css';
import {useEffect, useState} from "react";
import PostList from "./component/PostList/Post.List";
import { getAllPosts } from "./api";
import MyModal from "./component/UI/Modal/MyModal";
import MyInput from "./component/UI/MyInput/MyInput";
import MyButton from "./component/UI/MyButton/MyButton";
import PostForm from "./component/PostForm/PostForm";

function App() {
    const [posts, setPosts] = useState([

    ]);

    const getPosts = async () => {
        const posts = await getAllPosts();
        setPosts(posts)
    }

    useEffect(() => {
        getPosts()
    }, [])

    const createPost = (newPost) => {
        setPosts([...posts, newPost])
    }
    const deletePost = (post) => {
        posts.filter(p => p.id !== post.id)
    }

    return (
        <div className="App">
            <PostForm create={createPost} />
            <PostList deletePost={deletePost} posts={posts} title={'Список постов'} />
        </div>
    );
}

export default App;
