import React from 'react'
import './App.css';
import {useEffect, useState} from "react";
import PostList from "./component/PostList/Post.List";
import { getAllPosts } from "./api";
import MyModal from "./component/UI/Modal/MyModal";
import MyInput from "./component/UI/MyInput/MyInput";
import MyButton from "./component/UI/MyButton/MyButton";
import PostForm from "./component/PostForm/PostForm";
import MySelect from "./component/UI/MySelect/MySelect";

function App() {
    const [posts, setPosts] = useState([]);
    const [selectedSort, setSelectedSort] = useState('')

    const getPosts = async () => {
        const posts = await getAllPosts();
        setPosts(posts)
    }

    useEffect(() => {
        getPosts()
    }, [])

    const createPost = (newPost) => {
        setPosts([...posts, newPost])
    }
    const deletePost = (post) => {
        setPosts(posts.filter(p => p.id !== post.id));
    }
    const sortPost = (sort) => {
        setSelectedSort(sort)
        setPosts([...posts.sort()])
    }

    return (
        <div className="App">
            <PostForm create={createPost} />
            <hr style={{margin: "15px 0"}} />
            <div>
                <MySelect
                    value={selectedSort}
                    onChange={sortPost}
                    defaultValue={"Сортировка по"}
                    options={[
                        {value: 'title', name: 'По названию'},
                        {value: 'body', name: 'По описанию'}
                    ]}
                />
            </div>
            {posts.length
                ? <PostList deletePost={deletePost} posts={posts} title={'Список постов'} />
                : <h1>Постов не найдено :(</h1>
            }

        </div>
    );
}

export default App;
