import React from 'react'
import './App.css';
import {useEffect, useState} from "react";
import PostList from "./component/PostList/Post.List";
import { getAllPosts } from "./api";
import MyModal from "./component/UI/Modal/MyModal";
import MyInput from "./component/UI/MyInput/MyInput";
import MyButton from "./component/UI/MyButton/MyButton";

function App() {
    const [posts, setPosts] = useState([
        {id: 1, title: 'HTML', body: 'стандартизированный язык разметки'}
    ]);
    const [post, setPost] = useState({ title: '', body: '' })

    // const getPosts = async () => {
    //     const posts = await getAllPosts();
    //     setPosts(posts)
    // }
    //
    // useEffect(() => {
    //     getPosts()
    // }, [])

    const addNewPost = (e) => {
        e.preventDefault()
        setPosts([...posts, {id: Date.now(), ...post}])
    }

    return (
        <div className="App">
            <form>
                <MyInput
                    value={post.title}
                    onChange={e => setPost({...post, title: e.target.value})}
                    type="text"
                    placeholder='Введите название'
                />
                <MyInput
                    value={post.body}
                    onChange={e => setPost({...post, body: e.target.value})}
                    type="text"
                    placeholder='Введите текст'
                />
                <MyButton onClick={addNewPost}>Создать пост</MyButton>
            </form>
            <PostList posts={posts} title={'Список постов'} />
        </div>
    );
}

export default App;
